  font-size: 1.4rem;
  line-height: 1.5;
  color: white; /* Cambiado a blanco */
}

/* Clase para palabras con subrayado animado */
.underline-animation {
  position: relative;
  color: inherit;
  text-decoration: none;
  display: inline-block;
}


.underline-animation::after {
  content: '';
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: -2px;
  left: 0;
  background-color: currentColor;
  transform: scaleX(0);
  transform-origin: right;
  transition: transform 0.6s ease-out;
  animation: underlineAppear 1s ease-out forwards;
  animation-delay: 2s; /* Aparecerá después de 2 segundos */
}

@keyframes underlineAppear {
  from {
      transform: scaleX(0);
  }
  to {
      transform: scaleX(1);
  }
}

