document.addEventListener('DOMContentLoaded', function() {
    // Variables globales
    let map = null;
    let sankeyDiagram = null;
    let currentIngredient = null;
    let primerPlatoMap = null;
    let tooltipData = null;
    let categoryControlsVisible = false;

    // Inicializar visualizaciones
    function initializeVisualizations() {
        // Inicializar Sankey Diagram si existe el contenedor
        if (document.getElementById('network')) {
            sankeyDiagram = new SankeyDiagram('network');
        }

        // Inicializar Primer Plato Map si existe el contenedor
        if (document.getElementById('primer-plato-map')) {
            primerPlatoMap = new PrimerPlatoMap();
            primerPlatoMap.initialize('primer-plato-map');
        }

        // Inicializar mapa si existe el contenedor
        if (document.getElementById('map')) {
            map = L.map('map', {
                zoomControl: false,
                dragging: false,
                touchZoom: false,
                scrollWheelZoom: false
            }).setView([23.6345, -102.5528], 5);
        }
    }

    function createCategoryControls(categories) {
        if (!document.getElementById('network')) return;

        // Crear contenedor para controles si no existe
        let container = document.querySelector('.category-controls');
        if (!container) {
            container = document.createElement('div');
            container.className = 'category-controls';

            // Insertarlo antes del diagrama
            const networkDiv = document.getElementById('network');
            networkDiv.parentNode.insertBefore(container, networkDiv);
        } else {
            // Limpiar controles existentes
            container.innerHTML = '';
        }

        // Botón para "Todos"
        const allButton = document.createElement('button');
        allButton.className = 'category-button active';
        allButton.textContent = 'Todos';
        allButton.onclick = () => filterByCategory('all');
        container.appendChild(allButton);

        // Añadir botones para cada categoría
        const uniqueCategories = [...new Set(categories.classified_ingredients.map(item => item.category))];
        uniqueCategories.forEach(category => {
            const button = document.createElement('button');
            button.className = 'category-button';
            button.textContent = category;
            button.style.backgroundColor = categories.classified_ingredients.find(i => i.category === category)?.color || '#FFF';
            button.style.color = '#013971';
            button.onclick = () => filterByCategory(category);
            container.appendChild(button);
        });

        // Ocultar controles inicialmente
        toggleCategoryControls(categoryControlsVisible);
    }

    function toggleCategoryControls(show) {
        const controls = document.querySelector('.category-controls');
        if (controls) {
            controls.style.display = show ? 'flex' : 'none';
        }
        categoryControlsVisible = show;
    }

    function filterByCategory(category) {
        if (!sankeyDiagram) return;

        // Actualizar clases de los botones
        document.querySelectorAll('.category-button').forEach(button => {
            button.classList.toggle('active', button.textContent === (category === 'all' ? 'Todos' : category));
        });

        if (category === 'all') {
            // Mostrar todos los datos
            currentIngredient = null;
            sankeyDiagram.resetFilter();
        } else {
            // Filtrar por categoría
            const ingredientsInCategory = window.DataProcessorD3.ingredientCategories?.classified_ingredients
                .filter(item => item.category === category)
                .map(item => item.ingredient) || [];

            // Implementar lógica para filtrar por categoría en D3
            // Esto dependerá de la implementación específica
        }
    }

    function setupScrollama() {
        // Verificar si hay elementos para el scrollytelling
        const steps = document.querySelectorAll('.content-block');
        if (steps.length === 0) {
            console.warn('No se encontraron elementos para el scrollytelling');
            return null;
        }

        const scroller = scrollama();
        const ingredientesPermitidos = new Set(['Maíz', 'Frijol', 'Chile', 'Calabaza', 'Cacao']);
        let isLastIngredientPassed = false;

        scroller
            .setup({
                step: '.content-block',
                offset: 0.5,
                debug: false
            })
            .onStepEnter(response => {
                const { element, index, direction } = response;

                const ingrediente = element.querySelector('h3')?.textContent;
                const hasFlowerImage = element.querySelector('img.rotate-180');

                // Detectar si estamos en un bloque de ingrediente principal
                const isIngredienteBloque = ingrediente && hasFlowerImage && ingredientesPermitidos.has(ingrediente);

                // Detectar si hemos pasado el último ingrediente (Cacao)
                if (isIngredienteBloque && ingrediente === 'Cacao' && direction === 'down') {
                    isLastIngredientPassed = true;
                    toggleCategoryControls(true); // Mostrar filtros después de Cacao

                    // Mostrar el diagrama completo
                    if (sankeyDiagram) {
                        currentIngredient = null;
                        sankeyDiagram.resetFilter();
                    }
                } 
                // Si volvemos a subir antes del Cacao, ocultamos los filtros
                else if (ingrediente === 'Cacao' && direction === 'up') {
                    isLastIngredientPassed = false;
                    toggleCategoryControls(false);
                }

                // Actualizar el Sankey según el ingrediente actual
                if (sankeyDiagram && isIngredienteBloque) {
                    currentIngredient = ingrediente;
                    sankeyDiagram.filterByIngredient(ingrediente);
                } else if (sankeyDiagram && !isLastIngredientPassed) {
                    // Si no estamos en un bloque de ingrediente y no hemos pasado el último,
                    // reseteamos la visualización
                    currentIngredient = null;
                    sankeyDiagram.resetFilter();
                }

                // Actualizar mapa si existe
                if (primerPlatoMap) {
                    primerPlatoMap.updateVisualization(element);
                }
            });

        return scroller;
    }

    function setupMapInteractions(mexicoStates) {
        if (!map) return;

        L.geoJSON(mexicoStates, {
            style: {
                fillColor: '#3388ff',
                weight: 1,
                opacity: 1,
                color: 'white',
                fillOpacity: 0.7
            },
            onEachFeature: (feature, layer) => {
                layer.bindTooltip(feature.properties.name);

                layer.on('mouseover', function(e) {
                    layer.setStyle({
                        fillOpacity: 0.9,
                        weight: 2
                    });

                    // Integración con el nuevo SankeyDiagram
                    // Esta funcionalidad dependerá de la implementación específica
                });

                layer.on('mouseout', function() {
                    layer.setStyle({
                        fillOpacity: 0.7,
                        weight: 1
                    });

                    // Restaurar visualización original
                    if (currentIngredient && sankeyDiagram) {
                        sankeyDiagram.filterByIngredient(currentIngredient);
                    } else if (sankeyDiagram) {
                        sankeyDiagram.resetFilter();
                    }
                });
            }
        }).addTo(map);
    }

    // Inicialización
    initializeVisualizations();

    // Cargar datos y configurar visualizaciones
    Promise.all([
        fetch('./json/aperitivo.json'),
        fetch('./json/categorias_ingredientes.json'),
        fetch('./json/tooltip.json')
    ])
    .then(responses => Promise.all(responses.map(response => response.json())))
    .then(([sankeyJson, categoriesJson, tooltipsJson]) => {
        // Almacenar datos de categorías y tooltips
        window.DataProcessorD3.ingredientCategories = categoriesJson;
        tooltipData = tooltipsJson;

        // Procesar datos y actualizar diagrama Sankey
        if (sankeyDiagram) {
            const processedData = window.DataProcessorD3.processDataForSankey(sankeyJson);
            sankeyDiagram.tooltipData = tooltipsJson;
            sankeyDiagram.update(processedData);
        }

        // Crear controles de categorías
        createCategoryControls(categoriesJson);
        toggleCategoryControls(false); // Ocultar filtros inicialmente

        // Configurar scrollytelling
        setupScrollama();

        // Cargar datos del mapa si es necesario
        if (map) {
            fetch('json/cordenadas.geojson')
                .then(response => response.json())
                .then(mexicoStates => {
                    setupMapInteractions(mexicoStates);
                    map.fitBounds(L.geoJSON(mexicoStates).getBounds());
                })
                .catch(error => console.error('Error cargando GeoJSON:', error));
        }
    })
    .catch(error => console.error('Error cargando datos:', error));

    // Limpieza al cerrar
    window.addEventListener('beforeunload', () => {
        if (map) {
            map.remove();
            map = null;
        }
        if (sankeyDiagram) {
            sankeyDiagram.dispose();
            sankeyDiagram = null;
        }
        if (primerPlatoMap) {
            primerPlatoMap.dispose();
            primerPlatoMap = null;
        }
    });
});

