/**
 * Procesador de datos para el diagrama Sankey en D3
 */
class DataProcessorD3 {
    constructor() {
        this.currentFilter = null;
        this.allowedIngredients = new Set(['Maíz', 'Frijol', 'Chile', 'Calabaza', 'Cacao']);
        this.colors = {
            ingredientes: {
                'Maíz': '#F49A3F',
                'Frijol': '#2B6867',
                'Chile': '#F5584A',
                'Calabaza': '#F5A6CE',
                'Cacao': '#71C2F5',
                'default': '#98AFC7'
            },
            platillos: '#2e83bf',
            estados: '#013971',
            inactive: '#CCCCCC'
        };
    }

    /**
     * Procesa los datos para el formato que necesita D3-Sankey
     * @param {Object} rawData - Datos originales del archivo JSON
     * @returns {Object} - Datos procesados para D3-Sankey
     */
    processDataForSankey(rawData) {
        // Preparar nodos y enlaces
        const nodeMap = new Map();
        const links = [];

        // Primero crearemos un mapa de todos los nodos
        rawData.features.forEach(feature => {
            const alimentos = feature.properties.alimentos || [];
            const platillos = feature.properties.platillos || [];
            const estado = feature.properties.estado;

            // Añadir alimentos al mapa
            alimentos.forEach(alimento => {
                if (!nodeMap.has(alimento)) {
                    nodeMap.set(alimento, {
                        id: alimento,
                        name: alimento,
                        type: 'ingrediente',
                        connections: 0
                    });
                }
            });

            // Añadir platillos al mapa
            platillos.forEach(platillo => {
                if (!nodeMap.has(platillo)) {
                    nodeMap.set(platillo, {
                        id: platillo,
                        name: platillo,
                        type: 'platillo',
                        connections: 0
                    });
                }
            });

            // Añadir estado al mapa
            if (!nodeMap.has(estado)) {
                nodeMap.set(estado, {
                    id: estado,
                    name: estado,
                    type: 'estado',
                    connections: 0
                });
            }
        });

        // Ahora procesamos las relaciones
        rawData.features.forEach(feature => {
            const estado = feature.properties.estado;
            const platillos = feature.properties.platillos || [];

            // Procesar relaciones explícitas
            if (feature.properties.relaciones) {
                feature.properties.relaciones.forEach(relacion => {
                    if (nodeMap.has(relacion.origen) && nodeMap.has(relacion.destino)) {
                        links.push({
                            source: relacion.origen,
                            target: relacion.destino,
                            value: relacion.peso || 1
                        });

                        // Incrementar contador de conexiones
                        nodeMap.get(relacion.origen).connections += 1;
                        nodeMap.get(relacion.destino).connections += 1;
                    }
                });
            }

            // Conectar platillos con el estado
            platillos.forEach(platillo => {
                if (nodeMap.has(platillo) && nodeMap.has(estado)) {
                    links.push({
                        source: platillo,
                        target: estado,
                        value: 1
                    });

                    // Incrementar contador de conexiones
                    nodeMap.get(platillo).connections += 1;
                    nodeMap.get(estado).connections += 1;
                }
            });
        });

        // Convertir el mapa a un array de nodos
        const nodes = Array.from(nodeMap.values());

        return { nodes, links };
    }

    /**
     * Filtra los datos por ingrediente específico
     * @param {Object} sankeyData - Datos completos del Sankey
     * @param {string} ingrediente - Nombre del ingrediente para filtrar
     * @returns {Object} - Datos filtrados
     */
    filterByIngredient(sankeyData, ingrediente) {
        this.currentFilter = ingrediente;

        // Identificar nodos relevantes
        const relevantNodeIds = new Set([ingrediente]);
        const platillosRelacionados = new Set();
        const estadosRelacionados = new Set();

        // Identificar platillos directamente relacionados con el ingrediente
        sankeyData.links.forEach(link => {
            if (link.source === ingrediente || (link.source.id === ingrediente)) {
                const targetId = typeof link.target === 'object' ? link.target.id : link.target;
                relevantNodeIds.add(targetId);
                platillosRelacionados.add(targetId);
            }
        });

        // Identificar estados relacionados con esos platillos
        sankeyData.links.forEach(link => {
            const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
            if (platillosRelacionados.has(sourceId)) {
                const targetId = typeof link.target === 'object' ? link.target.id : link.target;
                relevantNodeIds.add(targetId);
                estadosRelacionados.add(targetId);
            }
        });

        // Filtrar nodos
        const filteredNodes = sankeyData.nodes.filter(node => {
            const nodeId = node.id || node.name;
            return relevantNodeIds.has(nodeId);
        }).map(node => {
            const nodeId = node.id || node.name;
            const nodeType = node.type || 
                (nodeId === ingrediente ? 'ingrediente' : 
                 platillosRelacionados.has(nodeId) ? 'platillo' : 'estado');

            return {
                ...node,
                id: nodeId,
                name: nodeId,
                type: nodeType,
                highlight: nodeId === ingrediente
            };
        });

        // Filtrar enlaces
        const filteredLinks = [];

        // Enlaces del ingrediente a platillos
        sankeyData.links.forEach(link => {
            const sourceId = typeof link.source === 'object' ? link.source.id : link.source;
            const targetId = typeof link.target === 'object' ? link.target.id : link.target;

            if (sourceId === ingrediente && platillosRelacionados.has(targetId)) {
                filteredLinks.push({
                    source: sourceId,
                    target: targetId,
                    value: 10 // Valor fijo para mejor visualización
                });
            }
            else if (platillosRelacionados.has(sourceId) && estadosRelacionados.has(targetId)) {
                filteredLinks.push({
                    source: sourceId,
                    target: targetId,
                    value: 10 // Valor fijo para mejor visualización
                });
            }
        });

        return { nodes: filteredNodes, links: filteredLinks };
    }

    /**
     * Restablece la visualización a todos los datos
     * @param {Object} sankeyData - Datos completos del Sankey
     * @returns {Object} - Datos procesados
     */
    resetVisualization(sankeyData) {
        this.currentFilter = null;
        return sankeyData;
    }

    /**
     * Obtiene el color para un nodo según su tipo
     * @param {Object} node - Nodo del Sankey
     * @returns {string} - Color en formato CSS
     */
    getNodeColor(node) {
        if (node.type === 'ingrediente') {
            if (node.name in this.colors.ingredientes) {
                return this.colors.ingredientes[node.name];
            }
            return this.colors.ingredientes.default;
        }
        else if (node.type === 'platillo') {
            return this.colors.platillos;
        }
        else if (node.type === 'estado') {
            return this.colors.estados;
        }
        return this.colors.inactive;
    }

    /**
     * Obtiene el color para un enlace
     * @param {Object} link - Enlace del Sankey
     * @returns {string} - Color en formato CSS
     */
    getLinkColor(link) {
        const source = typeof link.source === 'object' ? link.source : { name: link.source, type: 'unknown' };

        if (source.type === 'ingrediente') {
            if (source.name in this.colors.ingredientes) {
                return this.colors.ingredientes[source.name];
            }
            return this.colors.ingredientes.default;
        }
        else if (source.type === 'platillo') {
            return this.colors.platillos;
        }
        return this.colors.inactive;
    }
}

// Exportar como variable global
window.DataProcessorD3 = new DataProcessorD3();
